class CollageScreen extends StatefulWidget {
  @override
  _CollageScreenState createState() => _CollageScreenState();
}
class _CollageScreenState extends State<CollageScreen> with SingleTickerProviderStateMixin {
  final collageScreenController = Get.find<CollageScreenController>();

  late TabController _tabController;
  final GlobalKey key = GlobalKey();
  File? file;

  @override
  void initState() {
    super.initState();
    _tabController = TabController(vsync: this, length: 5);
  }

  @override
  void dispose() {
    _tabController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SafeArea(
        child: Stack(
          children: [
            MainBackgroundWidget(),
            Container(
              margin: EdgeInsets.only(left: 15, right: 15, bottom: 15),
              child: Column(
                children: [
                  appBar(),
                  SizedBox(height: 20),
                  Expanded(
                      child: RepaintBoundary(
                    key: key,
                    child: Container(
                      width: Get.width,
                      child: Obx(
                        () => collageScreenController.isLoading.value
                            ? Center(child: CircularProgressIndicator())
                            : ImageListModule(),
                      ),
                    ),
                  )),
                  tabBar(),
                  tabBarView(),
                ],
              ),
            )
          ],
        ),
      ),
    );
  }

  Widget appBar() {
    return Container(
      height: 50,
      width: Get.width,
      decoration: borderGradientDecoration(),
      child: Padding(
        padding: const EdgeInsets.all(3.0),
        child: Container(
            padding: EdgeInsets.only(left: 10, right: 10),
            decoration: containerBackgroundGradient(),
            child: Row(
              mainAxisAlignment: MainAxisAlignment.spaceBetween,
              children: [
                GestureDetector(
                  onTap: () {
                    collageScreenController.borderWidthValue.value = 0.0;
                    collageScreenController.activeColor.value = 0;
                    collageScreenController.borderRadiusValue.value = 0.0;
                    Get.back();
                  },
                  child: Container(child: Icon(Icons.close)),
                ),
                Container(
                  child: Text(
                    "Collage",
                    style: TextStyle(
                        fontFamily: "",
                        fontSize: 18,
                        fontWeight: FontWeight.bold),
                  ),
                ),
                GestureDetector(
                  onTap: () async {
                    //Get.back();
                    await _capturePng();
                  },
                  child: Container(
                    child: Image.asset(
                      Images.ic_downloading,
                      scale: 2,
                    ),
                  ),
                ),
              ],
            )),
      ),
    );
  }

  Widget tabBar() {
    return Container(
      child: TabBar(
        isScrollable: true,
        indicatorColor: Colors.transparent,
        indicatorSize: TabBarIndicatorSize.label,
        labelColor: Colors.black,
        labelPadding:
            EdgeInsets.only(top: 10.0, bottom: 5, left: 10, right: 10),
        unselectedLabelColor: Colors.black38,
        controller: _tabController,
        labelStyle: TextStyle(fontSize: 17),
        tabs: [
          Container(child: Tab(text: "Layout")),
          Container(child: Tab(text: "Border Width")),
          Container(child: Tab(text: "Border Color")),
          Container(child: Tab(text: "Border Radius")),
          Container(child: Tab(text: "WallPapers")),
        ],
      ),
    );
  }

  Widget tabBarView() {
    return Container(
      height: Get.height * 0.08,
      child: TabBarView(
        controller: _tabController,
        children: [
          LayoutScreen(),
          BorderWidthScreen(),
          BorderColorScreen(),
          BorderRadiusScreen(),
          WallPapersScreen(),
        ],
      ),
    );
  }

  Future _capturePng() async {
    try {
      print('inside');
      RenderRepaintBoundary boundary =
          key.currentContext!.findRenderObject() as RenderRepaintBoundary;
      print(boundary);
      ui.Image image = await boundary.toImage(pixelRatio: 3.0);
      print("image:===$image");
      final directory = (await getApplicationDocumentsDirectory()).path;
      ByteData? byteData =
          await image.toByteData(format: ui.ImageByteFormat.png);
      print("byte data:===$byteData");
      Uint8List pngBytes = byteData!.buffer.asUint8List();
      File imgFile = new File('$directory/photo.png');
      await imgFile.writeAsBytes(pngBytes);
      setState(() {
        file = imgFile;
      });
      print("File path====:${file!.path}");
      //collageScreenController.imageFileList = pngBytes;
      //bs64 = base64Encode(pngBytes);
      print("png Bytes:====$pngBytes");
      //print("bs64:====$bs64");
      //setState(() {});
      await saveImage();
    } catch (e) {
      print(e);
    }
  }

  Future saveImage() async {
    // renameImage();
    await GallerySaver.saveImage("${file!.path}",
        albumName: "OTWPhotoEditingDemo");
  }
}

